import React from 'react';
import styled from 'styled-components';

const PlayerContainer = styled.div`
  position: relative;
  padding-bottom: 56.25%; /* 16:9 aspect ratio */
  height: 0;
  overflow: hidden;
`;

const Iframe = styled.iframe`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border: none;
`;

function VideoPlayer({ imdbId, season, episode }) {
  let embedUrl = '';

  if (season && episode) {
    embedUrl = `https://vidsrc.xyz/embed/tv/${imdbId}/${season}-${episode}`;
  } else {
    embedUrl = `https://vidsrc.xyz/embed/movie/${imdbId}`;
  }

  // Prevent ads and popups from loading within the iframe
  const blockAdsAndPopups = () => {
    const iframeDoc = document.createElement('html');
    iframeDoc.innerHTML = '<body></body>';
    const iframeWin = iframeDoc.defaultView || window;
    iframeWin.addEventListener(
      'beforeload',
      (event) => {
        // Block known ad domains
        const blockedDomains = ['doubleclick.net', 'adservice.google.com','luminousstreamhaven.com','capaciousdrewreligion.com','recordedthereby.com','glersakr.com',
'googletagmanager.com','techtonicwave4.link','recordedthereby.com','scaredframe.com'];
        if (blockedDomains.some((domain) => new URL(event.url).hostname.includes(domain))) {
          event.preventDefault();
        }
      },
      true
    );
  };

  return (
    <PlayerContainer>
      <Iframe
        src={embedUrl}
        allowFullScreen
        ref={(iframe) => {
          if (iframe) {
            blockAdsAndPopups();
          }
        }}
      />
    </PlayerContainer>
  );
}

export default VideoPlayer;
