import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import MovieCard from '../components/MovieCard';
import { getPopularMovies, getTvShowDetails } from '../services/tmdbApi';

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 20px;
`;

function Home() {
     const [movies, setMovies] = useState([]);
     const [tvShows, setTvShows] = useState([]);

     useEffect(() => {
       getPopularMovies().then((response) => setMovies(response.data.results));
       getPopularTvShows().then((response) => setTvShows(response.data.results));
     }, []);

     return (
       <div>
         <h2>Popular Movies</h2>
         <Grid>
           {movies.map((movie) => (
             <MovieCard key={movie.id} movie={movie} />
           ))}
         </Grid>

         <h2>Popular TV Shows</h2>
         <Grid>
           {tvShows.map((tvShow) => (
             <MovieCard key={tvShow.id} movie={tvShow} />
           ))}
         </Grid>
       </div>
     );
   }

   export default Home;
