import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import tmdbApi from '../services/tmdbApi';

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 20px;
`;

const ActorCard = styled(Link)`
  background-color: #333;
  border-radius: 8px;
  overflow: hidden;
  text-decoration: none;
  color: white;
  transition: transform 0.3s ease;

  &:hover {
    transform: scale(1.05);
  }
`;

const ProfilePic = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
`;

const Name = styled.h3`
  padding: 10px;
  font-size: 16px;
  text-align: center;
`;

function Actors() {
  const [actors, setActors] = useState([]);

  useEffect(() => {
    tmdbApi.get('/person/popular').then((response) => setActors(response.data.results));
  }, []);

  return (
    <div>
      <h2>Popular Actors</h2>
      <Grid>
        {actors.map((actor) => (
          <ActorCard key={actor.id} to={`/actor/${actor.id}`}>
            <ProfilePic src={`https://image.tmdb.org/t/p/w500${actor.profile_path}`} alt={actor.name} />
            <Name>{actor.name}</Name>
          </ActorCard>
        ))}
      </Grid>
    </div>
  );
}

export default Actors;
