import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const SeriesDetail = () => {
    const { id } = useParams();
    const [series, setSeries] = useState(null);
    const [episodes, setEpisodes] = useState([]);

    useEffect(() => {
        const fetchSeries = async () => {
            const response = await axios.get(`https://api.themoviedb.org/3/tv/${id}`, {
                params: { api_key: 'YOUR_TMDB_API_KEY' },
            });
            setSeries(response.data);
        };

        fetchSeries();
    }, [id]);

    const fetchEpisodes = async (season_number) => {
        const response = await axios.get(`https://api.themoviedb.org/3/tv/${id}/season/${season_number}`, {
            params: { api_key: 'YOUR_TMDB_API_KEY' },
        });
        setEpisodes(response.data.episodes);
    };

    return series ? (
        <div>
            <h1>{series.name}</h1>
            <p>{series.overview}</p>
            <h2>Seasons</h2>
            {series.seasons.map((season) => (
                <button key={season.id} onClick={() => fetchEpisodes(season.season_number)}>
                    Season {season.season_number}
                </button>
            ))}
            <h3>Episodes</h3>
            <ul>
                {episodes.map((episode) => (
                    <li key={episode.id}>
                        {episode.episode_number}. {episode.name}
                    </li>
                ))}
            </ul>
        </div>
    ) : (
        <p>Loading...</p>
    );
};

export default SeriesDetail;
