import React, { useEffect } from 'react';
import styled from 'styled-components';

const PlayerContainer = styled.div`
  position: relative;
  padding-bottom: 56.25%; /* 16:9 aspect ratio */
  height: 0;
  overflow: hidden;
`;

const Iframe = styled.iframe`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  border: none;
`;

function VideoPlayer({ imdbId, season, episode }) {
  useEffect(() => {
    const injectCSS = () => {
      const iframe = document.querySelector('iframe');
      const doc = iframe.contentDocument || iframe.contentWindow.document;
      const style = document.createElement('style');

      // Custom CSS to hide ads or popups
      style.innerHTML = `
        .ad-container, .popup, .ads { display: none !important; }
      `;
      doc.head.appendChild(style);
    };

    const iframe = document.querySelector('iframe');
    iframe.addEventListener('load', () => {
      // Delay to ensure iframe content is fully loaded
      setTimeout(injectCSS, 1000);
    });

    return () => {
      iframe.removeEventListener('load', injectCSS);
    };
  }, []);

  let embedUrl = '';

  if (season && episode) {
    embedUrl = `https://vidsrc.xyz/embed/tv/${imdbId}/${season}-${episode}`;
  } else {
    embedUrl = `https://vidsrc.xyz/embed/movie/${imdbId}`;
  }

  return (
    <PlayerContainer>
      <Iframe src={embedUrl} allowFullScreen />
    </PlayerContainer>
  );
}

export default VideoPlayer;
