import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import MovieCard from '../components/MovieCard';
import { getPopularMovies, getTvShowDetails } from '../services/tmdbApi';

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 20px;
`;

function Home() {
  const [movies, setMovies] = useState([]);
  const [tvSeries, setTVSeries] = useState([]);

  useEffect(() => {
    getPopularMovies().then((response) => setMovies(response.data.results));
    getTvShowDetails().then((response) => setTVSeries(response.data.results));
  }, []);

  return (
    <div>
      <h2>Popular Movies</h2>
      <Grid>
        {movies.map((movie) => (
          <MovieCard key={movie.id} movie={movie} />
        ))}
      </Grid>

      <h2>Popular TV Series</h2>
      <Grid>
        {tvSeries.map((series) => (
          <MovieCard key={series.id} movie={series} />
        ))}
      </Grid>
    </div>
  );
}

export default Home;
