// components/Sidebar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FaHome, FaCompass, FaFilm, FaTv, FaUser, FaTimes, FaBars } from 'react-icons/fa';

const SidebarContainer = styled.nav`
  width: ${(props) => (props.isOpen ? '150px' : '25px')};
  background-color: #1a1a1a;
  padding: 20px;
  transition: width 0.3s ease;
  position: fixed;
  height: 100%;
`;

const NavItem = styled(Link)`
  display: flex;
  align-items: center;
  color: #fff;
  text-decoration: none;
  padding: ${(props) => (props.isOpen ? '10px 0' : '10px 0')};
  justify-content: ${(props) => (props.isOpen ? 'flex-start' : 'center')};
  &:hover {
    color: #ffd700;
  }
`;

const IconWrapper = styled.span`
  margin-right: ${(props) => (props.isOpen ? '10px' : '0')};
  display: flex;
  justify-content: center;
  width: ${(props) => (props.isOpen ? 'auto' : '100%')};
`;

const ToggleButton = styled.button`
  background: none;
  border: none;
  color: #fff;
  cursor: pointer;
  margin-bottom: 20px;
  font-size: 1.3rem;
  display: flex;
  align-items: center;
`;

const ContentWrapper = styled.div`
  margin-left: ${(props) => (props.isOpen ? '200px' : '60px')};
  transition: margin-left 0.3s ease;
  padding: 20px;
`;

function Sidebar() {
  const [isOpen, setIsOpen] = useState(true);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <>
      <SidebarContainer isOpen={isOpen}>
        <ToggleButton onClick={toggleSidebar}>
          {isOpen ? <FaTimes /> : <FaBars />}
        </ToggleButton>
        <NavItem to="/" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaHome /></IconWrapper> {isOpen && 'Home'}</NavItem>
        <NavItem to="/discovery" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaCompass /></IconWrapper> {isOpen && 'Discovery'}</NavItem>
        <NavItem to="/movies" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaFilm /></IconWrapper> {isOpen && 'Movies'}</NavItem>
        <NavItem to="/tv-shows" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaTv /></IconWrapper> {isOpen && 'TV Shows'}</NavItem>
        <NavItem to="/actors" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaUser /></IconWrapper> {isOpen && 'Actors'}</NavItem>
      </SidebarContainer>
      <ContentWrapper isOpen={isOpen}>
        {/* Other content goes here */}
      </ContentWrapper>
    </>
  );
}

export default Sidebar;
