import React from 'react';

const StreamingPlayer = ({ imdbId, tmdbId, season, episode }) => {
    const baseURL = "https://multiembed.mov/";

    const getIframeSrc = () => {
        if (tmdbId) {
            return `${baseURL}?video_id=${tmdbId}&tmdb=1${season ? `&s=${season}&e=${episode}` : ''}`;
        } else {
            return `${baseURL}?video_id=${imdbId}${season ? `&s=${season}&e=${episode}` : ''}`;
        }
    };

    return (
        <div className="player-wrapper">
            <iframe
                src={getIframeSrc()}
                width="100%"
                height="500px"
                frameBorder="0"
                allowFullScreen
                title="Streaming Player"
            />
        </div>
    );
};

export default StreamingPlayer;
