import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import styled, { ThemeContext } from 'styled-components';
import { FaHome, FaCompass, FaFilm, FaTv, FaUser, FaTimes, FaBars, FaPalette } from 'react-icons/fa';

const themes = {
  default: { name: 'Default', primary: '#ffd700', background: '#1a1a1a', text: '#ffffff' },
  nord: { name: 'Nord', primary: '#88C0D0', background: '#2E3440', text: '#D8DEE9' },
  gruvbox: { name: 'Gruvbox', primary: '#FE8019', background: '#282828', text: '#EBDBB2' },
  solarized: { name: 'Solarized', primary: '#B58900', background: '#002B36', text: '#839496' },
  dracula: { name: 'Dracula', primary: '#BD93F9', background: '#282A36', text: '#F8F8F2' },
  monokai: { name: 'Monokai', primary: '#F92672', background: '#272822', text: '#F8F8F2' },
  github: { name: 'GitHub', primary: '#0366D6', background: '#F6F8FA', text: '#24292E' },
  tomorrow: { name: 'Tomorrow', primary: '#F5871F', background: '#FFFFFF', text: '#4D4D4C' },
  oceanic: { name: 'Oceanic', primary: '#6699CC', background: '#1B2B34', text: '#D8DEE9' },
  material: { name: 'Material', primary: '#82AAFF', background: '#263238', text: '#EEFFFF' },
};

const SidebarContainer = styled.nav`
  width: ${(props) => (props.isOpen ? '200px' : '60px')};
  background-color: ${(props) => props.theme.background};
  padding: 20px 10px;
  transition: all 0.3s ease;
  position: fixed;
  height: 100%;
  overflow-y: auto;
  z-index: 1000;
`;

const NavItem = styled(Link)`
  display: flex;
  align-items: center;
  color: ${(props) => props.theme.text};
  text-decoration: none;
  padding: 10px;
  border-radius: 5px;
  margin-bottom: 5px;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${(props) => props.theme.primary};
    color: ${(props) => props.theme.background};
  }
`;

const IconWrapper = styled.span`
  margin-right: ${(props) => (props.isOpen ? '10px' : '0')};
  display: flex;
  justify-content: center;
  width: ${(props) => (props.isOpen ? 'auto' : '100%')};
`;

const ToggleButton = styled.button`
  background: none;
  border: none;
  color: ${(props) => props.theme.text};
  cursor: pointer;
  margin-bottom: 20px;
  font-size: 1.3rem;
  display: flex;
  align-items: center;
  width: 100%;
  justify-content: ${(props) => (props.isOpen ? 'flex-end' : 'center')};
`;

const ThemeSelector = styled.select`
  width: 100%;
  padding: 5px;
  margin-top: 10px;
  background-color: ${(props) => props.theme.background};
  color: ${(props) => props.theme.text};
  border: 1px solid ${(props) => props.theme.primary};
  border-radius: 5px;
`;

function Sidebar({ setTheme }) {
  const [isOpen, setIsOpen] = useState(false);
  const theme = useContext(ThemeContext);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleThemeChange = (e) => {
    setTheme(themes[e.target.value]);
  };

  return (
    <SidebarContainer isOpen={isOpen}>
      <ToggleButton onClick={toggleSidebar} isOpen={isOpen}>
        {isOpen ? <FaTimes /> : <FaBars />}
      </ToggleButton>
      <NavItem to="/" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaHome /></IconWrapper> {isOpen && 'Home'}</NavItem>
      <NavItem to="/discovery" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaCompass /></IconWrapper> {isOpen && 'Discovery'}</NavItem>
      <NavItem to="/movies" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaFilm /></IconWrapper> {isOpen && 'Movies'}</NavItem>
      <NavItem to="/tv-shows" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaTv /></IconWrapper> {isOpen && 'Series'}</NavItem>
      <NavItem to="/actors" isOpen={isOpen}><IconWrapper isOpen={isOpen}><FaUser /></IconWrapper> {isOpen && 'Actors'}</NavItem>
      {isOpen && (
        <div>
          <NavItem as="div"><IconWrapper isOpen={isOpen}><FaPalette /></IconWrapper> Theme</NavItem>
          <ThemeSelector onChange={handleThemeChange} value={theme.name.toLowerCase()}>
            {Object.keys(themes).map((key) => (
              <option key={key} value={key}>
                {themes[key].name}
              </option>
            ))}
          </ThemeSelector>
        </div>
      )}
    </SidebarContainer>
  );
}

export default Sidebar;
