// components/Sidebar.js
import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { FaHome, FaCompass, FaFilm, FaTv, FaUser } from 'react-icons/fa';

const SidebarContainer = styled.nav`
  width: 200px;
  background-color: #1a1a1a;
  padding: 20px;
`;

const NavItem = styled(Link)`
  display: flex;
  align-items: center;
  color: #fff;
  text-decoration: none;
  padding: 10px 0;
  &:hover {
    color: #ffd700;
  }
`;

const IconWrapper = styled.span`
  margin-right: 10px;
`;

function Sidebar() {
  return (
    <SidebarContainer>
      <NavItem to="/"><IconWrapper><FaHome /></IconWrapper> Home</NavItem>
      <NavItem to="/discovery"><IconWrapper><FaCompass /></IconWrapper> Discovery</NavItem>
      <NavItem to="/movies"><IconWrapper><FaFilm /></IconWrapper> Movies</NavItem>
      <NavItem to="/tv-shows"><IconWrapper><FaTv /></IconWrapper> TV Shows</NavItem>
      <NavItem to="/actors"><IconWrapper><FaUser /></IconWrapper> Actors</NavItem>
    </SidebarContainer>
  );
}

export default Sidebar;
