import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import MovieCard from '../components/MovieCard';
import { getPopularMovies, getPopularTVSeries, getPopularAnime } from '../services/tmdbApi';

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
  gap: 20px;
`;

function Home() {
  const [movies, setMovies] = useState([]);
  const [tvSeries, setTVSeries] = useState([]);
  const [anime, setAnime] = useState([]);

  useEffect(() => {
    getPopularMovies().then((response) => setMovies(response.data.results));
    getPopularTVSeries().then((response) => setTVSeries(response.data.results));
    getPopularAnime().then((response) => setAnime(response.data.results));
  }, []);

  return (
    <div>
      <h2>Popular Movies</h2>
      <Grid>
        {movies.map((movie) => (
          <MovieCard key={movie.id} movie={movie} />
        ))}
      </Grid>

      <h2>Popular TV Series</h2>
      <Grid>
        {tvSeries.map((series) => (
          <MovieCard key={series.id} movie={series} />
        ))}
      </Grid>

      <h2>Popular Anime</h2>
      <Grid>
        {anime.map((anime) => (
          <MovieCard key={anime.id} movie={anime} />
        ))}
      </Grid>
    </div>
  );
}

export default Home;
