// pages/MovieDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import { getMovieDetails, getMovieCredits, getMovieRecommendations } from '../services/tmdbApi';
import VideoPlayer from '../components/VideoPlayer';
import MovieCard from '../components/MovieCard';

const MovieContainer = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
  width: 100%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 20px;
`;

const MovieInfo = styled.div`
  display: flex;
  gap: 20px;
  flex-wrap: wrap;
`;

const Poster = styled.img`
  width: 300px;
  height: auto;
  object-fit: cover;
`;

const Info = styled.div`
  flex: 1;
  min-width: 300px;
`;


const CastContainer = styled.div`
  display: flex;
  overflow-x: auto;
  gap: 20px;
  padding: 20px 0;
  scrollbar-width: thin;
  scrollbar-color: #888 #000;

  &::-webkit-scrollbar {
    height: 8px;
  }

  &::-webkit-scrollbar-track {
    background: #000;
  }

  &::-webkit-scrollbar-thumb {
    background-color: #888;
    border-radius: 20px;
    border: 3px solid #000;
  }
`;

const CastMember = styled.div`
  text-align: center;
  width: 100px;
  flex-shrink: 0;
`;


const CastImage = styled.img`
  width: 100px;
  height: 150px;
  object-fit: cover;
  border-radius: 5px;
`;

const RecommendationsContainer = styled.div`
  display: flex;
  overflow-x: auto;
  gap: 20px;
  padding: 20px 0;
  scrollbar-width: thin;
  scrollbar-color: #888 #000;

  &::-webkit-scrollbar {
    height: 8px;
  }

  &::-webkit-scrollbar-track {
    background: #000;
  }

  &::-webkit-scrollbar-thumb {
    background-color: #888;
    border-radius: 20px;
    border: 3px solid #000;
  }
`;


function MovieDetails() {
  const { id } = useParams();
  const [movie, setMovie] = useState(null);
  const [cast, setCast] = useState([]);
  const [recommendations, setRecommendations] = useState([]);

  useEffect(() => {
    getMovieDetails(id).then((response) => setMovie(response.data));
    getMovieCredits(id).then((response) => setCast(response.data.cast.slice(0, 10)));
    getMovieRecommendations(id).then((response) => setRecommendations(response.data.results.slice(0, 10)));
  }, [id]);

  if (!movie) return <div>Loading...</div>;

  return (
    <MovieContainer>
      <MovieInfo>
        <Poster src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} alt={movie.title} />
        <Info>
          <h2>{movie.title}</h2>
          <p>{movie.overview}</p>
          <p>Release Date: {movie.release_date}</p>
          <p>Runtime: {movie.runtime} minutes</p>
          <p>Genres: {movie.genres.map(genre => genre.name).join(', ')}</p>
        </Info>
      </MovieInfo>
      <VideoPlayer imdbId={movie.imdb_id} />
      <h3>Cast</h3>
      <CastContainer>
        {cast.map((member) => (
          <CastMember key={member.id}>
            <CastImage src={`https://image.tmdb.org/t/p/w200${member.profile_path}`} alt={member.name} />
            <p>{member.name}</p>
          </CastMember>
        ))}
      </CastContainer>
      <h3>Recommendations</h3>
      <RecommendationsContainer>
        {recommendations.map((rec) => (
          <MovieCard key={rec.id} movie={rec} />
        ))}
      </RecommendationsContainer>
    </MovieContainer>
  );
}

export default MovieDetails;
